mapper.select:
select authorityName
from TO_USER_AUTHORITY
where userId = #{userId}

mapper.update:
update
    TO_USER_TOKEN
set
    expiredDate = #{expiredDate}
where token = #{token}

update TO_USER U
join TO_CHURCH CH on CH.id = U.churchId
join TO_USER_MANAGE_CHURCH UMC on UMC.churchId = U.churchId
join TO_USER_AUTHORITY UA on UA.userId = UMC.userId and UA.authorityName = 'ROLE_MANAGER'
set U.userStatus = #{userStatus}
where UMC.userId = #{managerUserId}
and U.id in

    #{item}

mapper.insert:
insert into TO_USER_YOUNG ( userId, baseYear ) select U.id, '2021' baseYear from ( select U.id from TO_USER U join TO_CHURCH CH on CH.id = U.churchId join TO_USER_MANAGE_CHURCH UMC on UMC.churchId = U.churchId join TO_USER_AUTHORITY UA on UA.userId = UMC.userId and UA.authorityName = 'ROLE_MANAGER' where UMC.userId = #{managerUserId} and U.id in
#{item}
) U left outer join TO_USER_YOUNG UY on UY.userId = U.id where UY.userId is null

mapper.update:
update TO_USER U
